import React, { useState, useEffect } from "react";
import { Card, Badge, Container, Row, ListGroup, Col, Tab, Nav } from 'react-bootstrap';
import { getVehiclesByReportNumber, getDriversByVehicleNumber } from "../api/api";
import "./crash-card.css";

export default function CrashCard({ crash }) {
    const [vehicleDetail, setVehicleDetail] = useState([]);

    const init = () => {
        getVehiclesByReportNumber(crash.report_number)
            .then(vehiclesData => {
                if (vehiclesData) {
                    vehiclesData.forEach(v => {
                        v.color = colorMap(v.color);
                        if (v) {
                            getDriversByVehicleNumber(crash.report_number, v.vehicle_number).then(driverData => {
                                let temp = {
                                    drivers: driverData
                                };
                                setVehicleDetail(vehicleDetail => [...vehicleDetail, { ...v, ...temp }]);
                            })
                        }
                    })
                }
            });
    };

    const colorMap = (color) => {
        switch (color) {
            case "WHT":
                return "White";
            case "BLU":
                return "Blue";
            case "BLK":
                return "White";
            case "GRN":
                return "Blue";
            case "SIL":
                return "White";
            default:
                return "Red";
        }
    }

    useEffect(() => {
        init();
    }, []);

    return (
        <Card>
            <Card.Header>
                <Container>
                    <Row>
                        <Badge variant="danger" className="float-right">{crash.crash_severity}</Badge>
                    </Row>
                    <Row>
                        {crash.on_street} @ {crash.city}, {crash.county}
                    </Row>
                </Container>
            </Card.Header>

            <Card.Body>
                {/* {vehicleDetail.map((vehicle) => (
                    <ListGroup.Item>
                        {vehicle.drivers.map((driver) => (
                            <div>
                                {driver.injury_severity} {driver.age} {driver.sex}
                            </div>
                        ))}
                        <div>
                            {vehicle.traveling_on_street} {vehicle.traveling_direction} {vehicle.maneuver}
                        </div>
                        <div>
                            {vehicle.color} {vehicle.year} {vehicle.make} {vehicle.model}
                        </div>
                    </ListGroup.Item>
                ))} */}
                <Tab.Container id="left-tabs-example" defaultActiveKey="1">
                    <Row>
                        <Col sm={3}>
                            <Nav  className="flex-column">
                                {vehicleDetail.map((vehicle) => (
                                    <Nav.Item>
                                        <Nav.Link eventKey={vehicle.vehicle_number}>Vehicle {vehicle.vehicle_number}</Nav.Link>
                                    </Nav.Item>
                                ))}
                            </Nav>
                        </Col>
                        <Col sm={9}>
                            <Tab.Content>
                                {vehicleDetail.map((vehicle) => (
                                    <Tab.Pane eventKey={vehicle.vehicle_number}>
                                        <div>
                                            {vehicle.color} {vehicle.year} {vehicle.make} {vehicle.model}
                                        </div>
                                        <div>
                                            {vehicle.traveling_on_street} {vehicle.traveling_direction} {vehicle.maneuver}
                                        </div>
                                        {vehicle.drivers.map((driver) => (
                                            <div>
                                                {driver.injury_severity} {driver.age} {driver.sex}
                                            </div>
                                        ))}


                                    </Tab.Pane>
                                ))}
                            </Tab.Content>
                        </Col>
                    </Row>
                </Tab.Container>
            </Card.Body>
        </Card>
    );
};
