import React, { useState, useEffect } from "react";
import Chart from 'react-apexcharts'
import { getCrashes } from "../api/api";

export default function Bar() {

    const countyTimeMap = {};

    const init = () => {
        getCrashes().then(data => {
            data.array.forEach(element => {
                let hour = element.crash_date_time.slice(11, 13);
                if (!countyTimeMap[element.county]) {
                    countyTimeMap[element.county] = {};
                }
                if (!countyTimeMap[element.county][hour]) {
                    countyTimeMap[element.county][hour] = 0;
                }
                countyTimeMap[element.county][hour]++;
            });
        });
    };

    useEffect(() => {
        init();
    }, []);

    // 
    // county : {0: 
    // 0}
    //
    // time 12-4
    const options = {
        xaxis: {
            categories: ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun']
        }
    }
    const series = [{
        name: 'series-1',
        data: [30, 40, 25, 50, 49, 21, 70, 51]
    }, {
        name: 'series-2',
        data: [23, 12, 54, 61, 32, 56, 81, 19]
    }]

    return (
        <div className="area">
            <Chart options={options} series={series} type="area" width="500" />
        </div>
    )
}
