import React, { useState, useEffect } from "react";
import { Card, Badge, Container, Row, Table, Col, Tab, Nav } from 'react-bootstrap';
import { getVehiclesByReportNumber, getDriversByVehicleNumber } from "../api/api";
import "./crash-card.css";

export default function CrashCard({ crash }) {
    const [vehicleDetail, setVehicleDetail] = useState([]);

    const init = () => {
        getVehiclesByReportNumber(crash.report_number)
            .then(vehiclesData => {
                if (vehiclesData) {
                    vehiclesData.forEach(v => {
                        v.color = colorMap(v.color);
                        if (v) {
                            getDriversByVehicleNumber(crash.report_number, v.vehicle_number).then(driverData => {
                                let temp = {
                                    drivers: driverData
                                };
                                setVehicleDetail(vehicleDetail => [...vehicleDetail, { ...v, ...temp }]);
                            })
                        }
                    })
                }
            });
    };

    const colorMap = (color) => {
        switch (color) {
            case "WHT":
                return "White";
            case "BLU":
                return "Blue";
            case "BLK":
                return "White";
            case "GRN":
                return "Blue";
            case "SIL":
                return "White";
            default:
                return "Red";
        }
    }

    const injuryMap = (injury) => {
        switch (injury) {
            case "Property Damage Only":
                return "success";
            case "None":
                return "success";
            case "Injury":
                return "warning";
            case "Serious Injury":
                return "warning";
            default:
                return "danger";
        }
    }

    useEffect(() => {
        init();
    }, []);

    return (
        <Card>
            <Card.Header>
                <Container>
                    <Row>
                        <Badge variant={injuryMap(crash.crash_severity)} className="float-right">{crash.crash_severity}</Badge>
                    </Row>
                    <Row>
                        {crash.on_street} @ {crash.city}, {crash.county}
                    </Row>
                </Container>
            </Card.Header>

            <Card.Body>
                {vehicleDetail.map((vehicle) => (
                    <Table striped bordered hover>
                        <tr>
                            <th>Vehicle {vehicle.vehicle_number}</th>
                            <td>{vehicle.color} {vehicle.year} {vehicle.make} {vehicle.model}</td>
                        </tr>
                        <tr>
                            <th>Description</th>
                            <td>Vehicle was {vehicle.maneuver} in {vehicle.traveling_on_street} heaing {vehicle.traveling_direction}</td>
                        </tr>
                        {vehicle.drivers.map((driver) => (
                            <tr>
                                <th>Person {driver.person_number}</th>
                                <td> {driver.age} {driver.sex} <Badge variant={injuryMap(crash.crash_severity)} className="float-right">{driver.crash_severity}</Badge> </td>
                            </tr>
                        ))}
                    </Table>

                ))}
            </Card.Body>
        </Card>
    );
};
