import React, { useState, useEffect } from "react";
import { Card, Button, Badge, Container, Row } from 'react-bootstrap';
import { getVehiclesByReportNumber, getDriversByVehicleNumber } from "../api/api";

export default function CrashCard({ crash }) {
    const [vehicleDetail, setVehicleDetail] = useState(null);

    const init = () => {
        let arr = [];
        getVehiclesByReportNumber(crash.report_number)
            .then(vehiclesData => {
                if (vehiclesData) {
                    vehiclesData.forEach(v => {
                        if (v) {
                            getDriversByVehicleNumber(crash.report_number, v.vehicle_number).then(driverData => {
                                let temp = { ...v, ...driverData };
                                arr.push(temp);
                            })
                        }
                    })
                }
            });
        setVehicleDetail(arr);
        console.log(vehicleDetail)
    };

    useEffect(() => {
        init();
    }, []);

    return (
        <Card>
            <Card.Header>
                <Container>
                    <Row>
                        <Badge variant="primary">{crash.crash_severity}</Badge>
                    </Row>
                    <Row>
                        {/* {vehicleDetail.length} */}
                    </Row>
                </Container>
            </Card.Header>
            {crash.on_street} @ {crash.city}, {crash.county}
            <Card.Body>
                {/* {vehicleDetail.map((crash) => (


        ))} */}

                <Card.Text>
                    With supporting text below as a natural lead-in to additional content.
    </Card.Text>
            </Card.Body>
        </Card>
    );
};
