import React, { useState, useEffect } from "react";
import Chart from 'react-apexcharts'
import { getCrashes } from "../api/api";

export default function Bar() {

    const countyTimeArray = [];

    const init = () => {
        const countyTimeMap = {};
        getCrashes().then(data => {
            data.forEach(element => {
                let hour = element.crash_date_time.slice(11, 13);
                if (!countyTimeMap[element.county]) {
                    countyTimeMap[element.county] = Array(24).fill(0);
                }
                countyTimeMap[element.county][hour - 1]++;
            });
            for (const [key, value] of Object.entries(countyTimeMap)) {
                countyTimeArray.push({
                    name: key,
                    data: value
                })
            }
            console.log(countyTimeArray);
        });
    };

    useEffect(() => {
        init();
    }, []);

    const options = {
        xaxis: {
            categories: ['12AM', '01AM', '02AM', '03AM', '04AM', '05AM', '06AM', '07AM', '08AM', '09AM', '10AM', '11AM', '12PM', '01PM', '02AM', '03AM', '04PM', '05AM', '06AM', '07PM', '08AM', '09AM', '10PM', '11AM']
        }
    }

    return (
        <div className="area">
            <Chart options={options} series={countyTimeArray} type="area" width="1000" />
        </div>
    )
}
