import React, { Component } from "react";
import { withGoogleMap, withScriptjs, GoogleMap, Marker, InfoWindow, TrafficLayer } from "react-google-maps";
import mapStyles from "./mapStyles";
import { getCrashes } from "../api/api";

require('dotenv').config();

class MapExample extends Component {
    constructor(props) {
        super(props);
        this.map = React.createRef();
        this.crashList = [];
    }

    componentDidUpdate() {
        getCrashes().then(data => {
            this.crashList = data;
            console.log(this.crashList)
        });
    }

    render() {
        const libraries = ["places"];
        const mapContainerStyle = {
            height: "60vh",
            width: "70vw",
        };
        const options = {
            styles: mapStyles,
            disableDefaultUI: true,
            zoomControl: true,
        };
        const center = {
            lat: 28,
            lng: -84,
        };
        const googleMapsApiKey = process.env.REACT_APP_GOOGLE_API_KEY;

        const GoogleMapExample = withScriptjs(
            withGoogleMap(props => (
                <GoogleMap
                    id="map"
                    mapContainerStyle={mapContainerStyle}
                    zoom={7.2}
                    options={options}
                    ref={map => {
                        this.map = map;
                    }}
                    onIdle={props.onMapIdle}
                    defaultCenter={center}
                    defaultZoom={3}
                >
                    {this.crashList.map((crash) => (
                        <Marker
                            key={crash.report_number}
                            position={{ lat: crash.latitude, lng: crash.longitude }}
                            // onClick={() => {
                            //     setSelected(crash);
                            // }}
                        />
                    ))}
                </GoogleMap>
            ))
        );
        return (
            <div>
                <GoogleMapExample
                    onMapIdle={() => {
                        let ne = this.map.getBounds().getNorthEast();
                        let sw = this.map.getBounds().getSouthWest();
                        console.log(ne.lat() + ";" + ne.lng());
                        console.log(sw.lat() + ";" + sw.lng());
                    }}
                    googleMapURL={`https://maps.googleapis.com/maps/api/js?key=${googleMapsApiKey}`}
                    loadingElement={<div style={{ height: `100%` }} />}
                    containerElement={<div style={{ height: `400px` }} />}
                    mapElement={<div style={{ height: `100%` }} />}
                />
            </div>
        );
    }
}

export default MapExample;
